    public List<TableComment> TableCommentList;
    protected override void OnInitialized()
    {
        TableCommentList = new List<TableComment>();
        TableCommentList.Add(new TableComment { TableNum = 1, Comment = null });
        TableCommentList.Add(new TableComment { TableNum = 2, Comment = null });
        foreach (var table in TableCommentList)
        {
            TransationModel transaction = TransactionService.transactionList.FirstOrDefault(a => Convert.ToInt32(a.TableNum) == table.TableNum);
            if (transaction != null)
            {
                decimal? total = 0;
                foreach (ArticleModel f in transaction.ArticleModels)
                {
                    total = total + f.TotalPrice;
                }
                if (total != 0)
                {
                    table.Comment = "未结账,金额为：" + total.ToString();
                }
            }
        }
    }
